export const isMenuHidden = () => {
    const menu = document.getElementById('menu')
    return menu.className == "menu visible"
}

export const reload = () => {
    setTimeout(() => {
        window.location.reload()
        if(flag)
            document.getElementById('menu').click()
      }, 100)
    const flag = isMenuHidden()
    console.log(flag)
}
