import React, { useState, useEffect } from 'react'
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg'
import './TaskPage.css'
import Calendar from 'react-calendar'
import DateTimePicker from 'react-datetime-picker';

const TaskPage = ({ match, history }) => {

    let taskId = match.params.id
    let [task, setTask] = useState(null)
    let [show, setShow] = useState(false)
    
    useEffect(() => {
        getTask()
    }, [taskId, null])

    const showMode = () => {
        setShow(true)
    }

    const onChange = (e, selectedDate) => {
        const currentDate = selectedDate || date;
        setDeadline(currentDate)
        console.log("DATE")
        console.log(currentDate)
    }
    
    let getTask = async () => {
        if (taskId === 'new') return
        let response = await fetch(`/api/tasks/${taskId}/`)
        let data = await response.json()
        setTask(data)
    }

    let createTask = async () => {
        fetch(`/api/tasks/`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(task)
        })
    }

    let updateTask = async () => {
        fetch(`/api/tasks/${taskId}/`, {
            method: "PUT",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(task)
        })
    }

    let deleteTask = async () => {
        fetch(`/api/tasks/${taskId}/`, {
            method: 'DELETE',
            'headers': {
                'Content-Type': 'application/json'
            }
        })
        history.push('/')
    }

    let handleSubmit = () => {
        if (taskId !== 'new' && task.description === '') {
            deleteTask()
        } else if (taskId !== 'new') {
            updateTask()
        } else if (task?.description && taskId === 'new' && task.description !== null) {
            createTask()
        }
        history.push('/')
    }

    let handleChange = (value) => {
        setTask(task => ({ ...task, 'description': value }))
    }

    let changeTitle = (value) => {
        setTask(task => ({ ...task, 'title': value }))
    }

    let setDeadline = (value) => {
        setTask(task => ({ ...task, 'deadline': value }))
    }

    let handleCheck = () => {
        
        setTask(task => ({...task, 'completed': !task.completed}))
    }

    let getDeadline = (task) => {
        return new Date(task?.deadline).toLocaleDateString()
    }
    
    return (
        <div className="task" >
            <div className="task-header">
                <h3>
                    <ArrowLeft onClick={handleSubmit} />
                </h3>
                {taskId !== 'new' ? (
                    <button onClick={deleteTask}>Delete</button>
                ) : (
                    <button onClick={handleSubmit}>Done</button>
                )}
                </div>
                
            <label>Task:</label>
                <textarea className='textarea title' onChange={(e) => { changeTitle(e.target.value) }} value={task?.title}></textarea>
            <br></br>
            <label>
                Status: <input className='checkbox task'
                type="checkbox"
                checked={task?.completed}
                onChange={handleCheck}
                /> 
            </label> 
            <br></br>
            <label>
                Deadline: 
                <DateTimePicker 
                mode = 'date'
                onChange={onChange} 
                value={task?.date}
                >

                </DateTimePicker>
                console.log(task?.date)
                
            </label>
            <br></br>
            <h1>
                Description
            </h1>
            <label>
                <textarea className='textarea body' onChange={(e) => { handleChange(e.target.value) }} value={task?.description}></textarea>
            </label>
        </div>
    )
}

export default TaskPage
