import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import "./ListItem.css"
import DropdownItem from '../DropDownItem.js'


const DropDownComponent = ({selected, setSelected}) => {

    const [isActive, setIsActive] = useState(false)
    const [projects, setProjects] = useState([])

    const test = (projects) => {
        projects.then((result) => {
            console.log(result)
        })
    }

    useEffect(() => {
        getProjectsArray().then((result) =>{
            setProjects
        })
    })

    const getProjectsArray = async () => {
        
        let response = await fetch(`/api/projects/`)
        let data = await response.json()
        return await data
    }

    return (
        <div className='dropdown'>
            <div className='dropdown-btn' onClick = {e => test(projects)}>
                Test
            </div>
            <div className='dropdown-btn' onClick = {e => setIsActive(!isActive)}>
                {
                    selected !== null ?
                    "Inbox": "Inbox"
                }
                <span className='fas fa-caret-down'/>
            </div>
            {isActive && (
                <div className='dropdown-content'>
                    {   
                        getProjectsArray(projects).then((result) => {
                            for(var i=0;i<result.length; i++){
                                let project = result[i]["name"];
                                <div onClick = {(e) => {
                                    setSelected(project.name)
                                    setIsActive(false)
                                    }
                                }
                                className='dropdown-item'>
                                    {project.name}
                                </div>
                            }
                           
                        })
                    }
            </div>
            )}

        </div>
    )
}

export default DropDownComponent
