import {
  HashRouter as Router,
  Route
} from "react-router-dom";

import React, {useState} from 'react';

import './App.css';
import Header from './components/Header'
import NotesListPage from './pages/TasksListPage'
import TaskPage from './pages/TaskPage'

function App() {
  const [burger_class, setBurgerClass] = useState("burger-bar unlicked")
  const [menu_class, setMenuClass] = useState("menu hidden")
  const [isMenuClicked, setIsMenuClicked] = useState(false)
  const [mainContainer, setContainerWidth] = useState("main-container full-width dark")

  const updateMenu = () =>{
      if(!isMenuClicked) {
          setBurgerClass("burger-bar clicked")
          setMenuClass("menu visible")
          setContainerWidth("main-container shrinked dark")
      }
      else {
          setBurgerClass("burger-bar unclicked")
          setMenuClass("menu hidden")
          setContainerWidth("main-container full-width dark")
      }
      setIsMenuClicked(!isMenuClicked)
  }
  return(
    <Router>

      <div>
        <div>
          <div style={{width: '100}', height:'vh'}}> 
              <nav>
                  <div className="burger-menu" onClick={updateMenu}>
                      <div className={burger_class}></div>
                      <div className={burger_class} ></div>
                      <div className={burger_class} ></div>
                  </div>
              </nav>
              <div className={mainContainer}>

            <div className="app">

              <Route path="/" exact component={NotesListPage} />
              <Route path="/note/:id" component={TaskPage} />
            </div>
          </div>
              <div className={menu_class}></div>
          </div>
          </div>
        </div>
          </Router>
  )
}

export default App;
