import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import './ListProject.css'
import { reload } from './../Helper.js'
import EditButton from './EditButton.js'
import DeleteButton from './DeleteButton.js'
import CancelButton from './CancelButton.js'
import ConfirmButton from './ConfirmButton.js'

const ListProject = ({ project: project, refresh}) => {

    const deleteProject = () =>{
        alert(project.name)
    }

    const [readOnly, setReadOnly] = useState(true)
    const [text, setText] = useState(project.name)
    const [prj, setPrj] = useState(project)

    const changeReadOnly = () => {
        setReadOnly(!readOnly)
    }

    const changeProjectName = () => {
        project.name = text
    }

    return (
        
        <Link to={`/project/${project.name}/`}>
            <div className="projects-item">
                    <ul className='left-side'>
                        {
                            readOnly?
                            <h3>{project.name}</h3>: <input type="text" defaultValue={text} onChange={setText(e.target.value)}></input>
                        }
                    </ul>
                    {
                        readOnly?
                        <ul className='right-side'>
                            <EditButton onClick={changeReadOnly}/>
                            <DeleteButton onClick={deleteProject}/>
                        </ul>:
                        <ul className='right-side'>
                            <ConfirmButton onClick={changeProjectName}/>
                            <CancelButton onClick={changeReadOnly}/>
                        </ul>

                    }
            </div>
        </Link>
    )
}

export default ListProject
