import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import "./ListItem.css"
import DropdownItem from '../DropDownItem.js'
import { IconName } from "react-icons/fa";
import './AddButton.css'
import { ReactComponent as DownArrow } from '../assets/down-arrow.svg'
import './DropDownComponent.css'
const options = ['a', 'b', 'c']

const DropDownComponent = ({selected, setSelected, getProjects}) => {

    const [isActive, setIsActive] = useState(false)
    const [projects, setProjects] = useState(null)

    const test = (projects) => {
        projects.then((result) => {
            console.log(result)
        })
    }

    useEffect(() => {
        if(projects == null){
            setProjects(getProjectsArray.then())
        }
    })

    const getProjectsArray = async () => {
        
        let response = await fetch(`/api/projects/`)
        let data = await response.json()
        return await data
    }

    return (
        <div className='dropdown'>
            <div className='dropdown-btn' onClick = {e => setIsActive(!isActive)}>
                <h3>
                    {
                        selected == null ?
                        "Inbox": selected
                    }
                </h3>
                <div className="dropdown-button-icon">
                    <DownArrow/>
                </div>
            </div>
            {isActive && (
                <div className='dropdown-content'>
                    {
                    
                    // projects.map(option => (
                    //     <div onClick = {(e) => {
                    //         setSelected(option["name"])
                    //         console.log(selected)
                    //         setIsActive(false)
                    //         }
                    //     }
                    //     className='dropdown-item' key={option["name"]}>
                    //         {option["name"]}
                    //     </div>
                    // ))
                    }
                </div>
            )}

        </div>
    )
}

export default DropDownComponent
