import React, { useState, useEffect } from 'react'
import ListItem from './../components/ListItem.js'
import AddButton from './../components/AddButton.js'
import './TasksListPage.css'

const TasksListPage = ({match, history}) => {

    let projectId = match.params.id
    let [tasks, setTasks] = useState([])

    useEffect(() => {
        getTasks()
    }, [])


    let getTasks = async () => {

        let response = await fetch(`/api/project/${projectId}`)
        let data = await response.json()
        setTasks(data)
        console.log(projectId)
    }

    return (
        <div className="tasks">
            <div className="task-header">
                <h2 className="tasks-title">&#9782; Tasks</h2>
                <p className="tasks-count">{tasks.length}</p>
            </div>

            <div className="tasks-list">
                 {tasks.map((task, index) => (
                    <label>
                            <ListItem key={index} task={task} />
                    </label>
                ))}
            </div>
            <AddButton/>
        </div>
    )
}

export default TasksListPage
