import React, { useState, useEffect } from 'react'
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg'

const TaskPage = ({ match, history }) => {

    let taskId = match.params.id
    let [task, setTask] = useState(null)
    const [checked, setChecked] = React.useState(false);

    useEffect(() => {
        getTask()
    }, [taskId])

    const Checkbox = ({ label, value, onChange }) => {
        return (
          <label>
            <input type="checkbox" checked={value} onChange={onChange} />
            {label}
          </label>
        );
    };

    let getTask = async () => {
        if (taskId === 'new') return
        let response = await fetch(`/api/tasks/${taskId}/`)
        let data = await response.json()
        setTask(data)
    }

    let createTask = async () => {
        fetch(`/api/tasks/`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(task)
        })
    }


    let updateTask = async () => {
        fetch(`/api/tasks/${taskId}/`, {
            method: "PUT",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(task)
        })
    }

    let toggleCheckbox = (label) => {
        console.log('toggle')
    }

    let deleteTask = async () => {
        fetch(`/api/tasks/${taskId}/`, {
            method: 'DELETE',
            'headers': {
                'Content-Type': 'application/json'
            }
        })
        history.push('/')
    }

    let handleSubmit = () => {
        if (taskId !== 'new' && task.description === '') {
            deleteTask()
        } else if (taskId !== 'new') {
            updateTask()
        } else if (task?.description && taskId === 'new' && task.description !== null) {
            createTask()
        }
        history.push('/')
    }

    let handleChange = (value) => {
        setTask(task => ({ ...task, 'description': value }))
    }

    return (
        <div className="note" >
            <div className="note-header">
                <h3>
                    <ArrowLeft onClick={handleSubmit} />
                </h3>
                {taskId !== 'new' ? (
                    <button onClick={deleteTask}>Delete</button>
                ) : (
                    <button onClick={handleSubmit}>Done</button>
                )}

                </div>
            <textarea className='textarea title' onChange={(e) => { handleChange(e.target.value) }} value={task?.title}></textarea>

            <label>

                <input className='checkbox task'
                type="checkbox"
                checked={checked}
                onChange={handleChange}
                />
                <textarea className='textarea body' onChange={(e) => { handleChange(e.target.value) }} value={task?.description}></textarea>
            </label>
        </div>
    )
}

export default TaskPage
