import React from 'react'
import { Link } from 'react-router-dom'
import "./ListItem.css"


let getDeadline = (task) => {
    return new Date(task.deadline).toLocaleDateString()
}

let getTitle = (task) => {

    let title = task.title.split('\n')[0]
    console.log(title)
    if (title.length > 45) {
        return title.slice(0, 45)
    }
    return title
}

let getContent = (task) => {
    let title = getTitle(task)
    let content = task.description.replaceAll('\n', ' ')
    content = content.replaceAll(title, '')

    if (content.length > 45) {
        return content.slice(0, 45) + '...'
    } else {
        return content
    }
}

const ListItem = ({task: task, project: project}) => {
    return (
        <Link to={`task/${task.id}`}>
            <div className="list-item" >
                <h3>{getTitle(task)}</h3>
                <p><span>Deadline: {getDeadline(task)}</span></p><p>{getContent(task)}</p>
            </div>

        </Link>
    )
}

export default ListItem
